        selectorArchivos.setFileFilter(Filtro);
        selectorArchivos.showOpenDialog(this);
        File archivo = selectorArchivos.getSelectedFile(); // obtiene el archivo seleccionado
        if ((archivo == null) || (archivo.getName().equals(""))) {
            JOptionPane.showMessageDialog(this, "Nombre de archivo inv叩lido", "Nombre de archivo inv叩lido", JOptionPane.ERROR_MESSAGE);
        }  
        if(archivo!=null){
        Scanner scn = null;
        try {
            scn = new Scanner(archivo);
        } catch (FileNotFoundException ex) {
            Logger.getLogger(GUI.class.getName()).log(Level.SEVERE, null, ex);
        }
        while (scn.hasNext()) {
            JT.insert(scn.nextLine() + "\n", JT.getText().length());
        }
       }
        return JT.getText();
